var ExtractTextPlugin = require('extract-text-webpack-plugin');


module.exports = function (env) {
    return webpackMerge(baseConfig(env), {
        plugins: [
            new webpack.LoaderOptionsPlugin({
                minimize: true,
                debug: false
            }),

            // webpack内置js压缩插件;
            new webpack.optimize.UglifyJsPlugin({
